INSERT INTO `jos_content` VALUES (14, 91, 'Global Quality Compliance', 0x676c6f62616c2d7175616c6974792d636f6d706c69616e6365, '<p align="justify">Quality assurance is a fundamental commitment from Jindal Buttons made to all customers. We ensure the same. We have participated in TQM process with our customers. We own a state of the art in house testing facility relevant to buttons testing processes in its entirety. We ensure complete traceability on product safety and compliance.</p>\r\n<ul>\r\n<li>\r\n<div align="justify">All products manufactured in India, China completely conform to The Consumer Product Safety Improvement Act (CPSIA) statutes relevant to the products we manufacture.</div>\r\n</li>\r\n<li>\r\n<div align="justify">REACH : a regulation of the European Union, adopted to improve the protection of human health and environment from risks posed by chemicals, places the burden of proof and the onus of compliance on the manufacturer. Jindal Polybuttons takes responsibility of the associated obligations and compliance.</div>\r\n</li>\r\n<li>\r\n<div align="justify">California Proposition 65- Better known as Proposition 65 includes approximately 800 chemicals. We extend our commitment towards compliance to all customers doing business in California.</div>\r\n</li>\r\n<li>\r\n<div align="justify">Conflict Minerals is collectively termed for Tantalum, Cassiterite (Tin), Gold, Tungsten. As per the conversion of the Dodd Frank Consumer Protection act into law in 2010, we have signed the contract of  commitment Non usage and non sourcing of these material from the particular part of the world, with our Customers.</div>\r\n</li>\r\n</ul>\r\n<div align="center">\r\n<div style="float: left; height: 50%; width: 30%; padding: 6px 6px 6px 20%;">1. Oekotex <img style="height: 100%; width: 100%;" src="images/oekotex2015.jpg" alt="" /></div>\r\n<div style="float: left; height: 50%; width: 30%; padding: 6px;">2. TQP <img style="height: 100%; width: 100%;" src="images/tqp_report.jpg" alt="" /></div>\r\n</div>', '', 1, 15, '2015-05-14 08:35:23', 42, '', '2015-06-08 05:24:11', 42, 0, '0000-00-00 00:00:00', '2015-05-14 08:35:23', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 19, 0, '', '', 1, 424, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', '');
INSERT INTO `jos_content` VALUES (15, 96, 'Corporate Social Responsibility & Sustainability', 0x6373722d7375737461696e6162696c697479, '<p align="justify"><strong>The company takes pride in its ongoing efforts towards fulfilling its responsibilities by –</strong></p>\r\n<div align="justify">\r\n<ul>\r\n<li>Adapting processes that are least harmful to the environment</li>\r\n<li>Minimal consumption of electricity</li>\r\n<li>Recycling of larges masses of water without wastage. In house waste water treatment plant with zero ground disposal</li>\r\n<li>Mass plantation of trees within its large premises and outside</li>\r\n<li>Rain water harvesting</li>\r\n<li>Continuous mandatory testing of raw material to ensure compliance with international chemical restrictions</li>\r\n</ul>\r\n</div>\r\n<p align="justify"><strong>Ethical Operating processes :</strong></p>\r\n<div align="justify">\r\n<ul>\r\n<li>No Child labor employment</li>\r\n<li>No involuntary labour</li>\r\n<li>Stringently controlled working hours</li>\r\n<li>Provision for safe and hygienic work environment</li>\r\n<li>Provision for  employment benefits</li>\r\n<li>Extremely strict environment, human safety  protection  </li>\r\n<li>Animal welfare policies</li>\r\n<li>Promoting education, health care, eradicating hunger, poverty and malnutrition at a macro level.</li>\r\n</ul>\r\n</div>', '', 1, 16, '2015-05-14 08:39:26', 42, '', '2015-09-01 10:46:07', 42, 0, '0000-00-00 00:00:00', '2015-05-14 08:39:26', '0000-00-00 00:00:00', '{"image_intro":"","float_intro":"","image_intro_alt":"","image_intro_caption":"","image_fulltext":"","float_fulltext":"","image_fulltext_alt":"","image_fulltext_caption":""}', '{"urla":false,"urlatext":"","targeta":"","urlb":false,"urlbtext":"","targetb":"","urlc":false,"urlctext":"","targetc":""}', '{"show_title":"","link_titles":"","show_tags":"","show_intro":"","info_block_position":"","show_category":"","link_category":"","show_parent_category":"","link_parent_category":"","show_author":"","link_author":"","show_create_date":"","show_modify_date":"","show_publish_date":"","show_item_navigation":"","show_icons":"","show_print_icon":"","show_email_icon":"","show_vote":"","show_hits":"","show_noauth":"","urls_position":"","alternative_readmore":"","article_layout":"","show_publishing_options":"","show_article_options":"","show_urls_images_backend":"","show_urls_images_frontend":""}', 6, 0, '', '', 1, 394, '{"robots":"","author":"","rights":"","xreference":""}', 0, '*', '');
