INSERT INTO `jos_ucm_history` VALUES (55, 19, 1, '', '2015-06-02 07:17:13', 42, 4226, '2eaa3819909b4cee6282d413bdf133711fd098ea', '{"id":19,"asset_id":"101","title":"About Us","alias":"about-us","introtext":"<p><strong>About Jindal Buttons :<\\/strong><\\/p>\\r\\n<p align=\\"justify\\">The S R Jindal Group, founded by The Late Mr Shiv Ram Jindal in the year 1960, is a group with diversified manufacturing verticals, for steel pipes, metal strips, chemicals, synthetic leather and garment accessories.<\\/p>\\r\\n<p align=\\"justify\\">Group Companies :<\\/p>\\r\\n<div align=\\"justify\\">\\r\\n<ul>\\r\\n<li>Jindal Polybuttons Pvt Ltd<\\/li>\\r\\n<li>Ravindra Tubes Limited<\\/li>\\r\\n<li>Jindal Structures Limited<\\/li>\\r\\n<li>Rose valley Polymers Pvt Ltd<\\/li>\\r\\n<li>Hisar Metal Industries Ltd<\\/li>\\r\\n<li>United Decoratives Pvt Ltd<\\/li>\\r\\n<li>United Polygenious Pvt Ltd<\\/li>\\r\\n<li>Jindal Agro Pvt Ltd<\\/li>\\r\\n<li>S R Jindal Industries Pvt Ltd<\\/li>\\r\\n<\\/ul>\\r\\n<\\/div>\\r\\n<p align=\\"justify\\">Jindal Polybuttons Ltd, incorporated in 1992 with a vision of being \\u201cworld\\u2019s most preferred trim solution partner to the global apparel industry\\u201d. In its 23rd year, rechristened as Jindal Polybuttons Pvt Ltd (in 2014), the company, has grown to become a brand that is a one stop sourcing point, \\u00a0which continues to provide a super efficient supply chain with its vertical manufacturing set up including in-house polyester-resin and pigment manufacturing competencies. Also included in its capabilities is a vertical Metal Trims manufacturing plant complete with Power Presses, die casting, tool room facilities for mould making, in house state of the art plating facility together with a zero ground discharge effluence treatment plant.<\\/p>\\r\\n<p align=\\"justify\\">Jindal Polybuttons continues to provide superfine global quality garment trims at competitive prices, creating a super efficient supply chain, whilst meeting the most stringent compliance requirements set by federal global agencies, adopted by all global brands.<\\/p>\\r\\n<p align=\\"justify\\">The Company, amongst its various proud achievements, can boast of escalating the industry standards many notches by its in depth continuous research towards improvement, quality compliance procedures and traceability factors, achieving all of this while looking out for aesthetics in design components.<\\/p>\\r\\n<p align=\\"justify\\">The product range includes almost all types of Poly Buttons, Metal Buttons, Jeans buttons, Shanks, Snaps, metal plates, badges, D rings etc., with in-house capabilities including complete button making process for Polybuttons, Die Casting for Alloy as well as Brass buttons, Snaps, variety of Shanks. The facility includes laser engraving for polyester buttons.<\\/p>","fulltext":"","state":1,"catid":"9","created":"2015-05-14 08:45:53","created_by":"42","created_by_alias":"","modified":"2015-06-02 07:17:13","modified_by":"42","checked_out":"42","checked_out_time":"2015-06-02 07:16:28","publish_up":"2015-05-14 08:45:53","publish_down":"0000-00-00 00:00:00","images":"{\\"image_intro\\":\\"\\",\\"float_intro\\":\\"\\",\\"image_intro_alt\\":\\"\\",\\"image_intro_caption\\":\\"\\",\\"image_fulltext\\":\\"\\",\\"float_fulltext\\":\\"\\",\\"image_fulltext_alt\\":\\"\\",\\"image_fulltext_caption\\":\\"\\"}","urls":"{\\"urla\\":false,\\"urlatext\\":\\"\\",\\"targeta\\":\\"\\",\\"urlb\\":false,\\"urlbtext\\":\\"\\",\\"targetb\\":\\"\\",\\"urlc\\":false,\\"urlctext\\":\\"\\",\\"targetc\\":\\"\\"}","attribs":"{\\"show_title\\":\\"\\",\\"link_titles\\":\\"\\",\\"show_tags\\":\\"\\",\\"show_intro\\":\\"\\",\\"info_block_position\\":\\"\\",\\"show_category\\":\\"\\",\\"link_category\\":\\"\\",\\"show_parent_category\\":\\"\\",\\"link_parent_category\\":\\"\\",\\"show_author\\":\\"\\",\\"link_author\\":\\"\\",\\"show_create_date\\":\\"\\",\\"show_modify_date\\":\\"\\",\\"show_publish_date\\":\\"\\",\\"show_item_navigation\\":\\"\\",\\"show_icons\\":\\"\\",\\"show_print_icon\\":\\"\\",\\"show_email_icon\\":\\"\\",\\"show_vote\\":\\"\\",\\"show_hits\\":\\"\\",\\"show_noauth\\":\\"\\",\\"urls_position\\":\\"\\",\\"alternative_readmore\\":\\"\\",\\"article_layout\\":\\"\\",\\"show_publishing_options\\":\\"\\",\\"show_article_options\\":\\"\\",\\"show_urls_images_backend\\":\\"\\",\\"show_urls_images_frontend\\":\\"\\"}","version":3,"ordering":"0","metakey":"","metadesc":"","access":"1","hits":"32","metadata":"{\\"robots\\":\\"\\",\\"author\\":\\"\\",\\"rights\\":\\"\\",\\"xreference\\":\\"\\"}","featured":"0","language":"*","xreference":""}', 0);
INSERT INTO `jos_ucm_history` VALUES (56, 12, 1, '', '2015-06-02 07:20:56', 42, 3482, 'f7dee0a7cab5226b062401e13a56fddcaa7987de', '{"id":12,"asset_id":"83","title":"Management Desk","alias":"management-desk","introtext":"<p>Dear Customer,<\\/p>\\r\\n<p align=\\"justify\\">Leadership management is the art of making problems enjoyable and interesting to deal with, by allowing the team to have the power and accountability to resolve them.<\\/p>\\r\\n<p align=\\"justify\\">At Jindal Polybuttons, our core beliefs and competencies are aimed towards developing a high performance organization that creates benchmarks in terms of value in the industry. We remain customer focused and continually endeavor to deliver more than the customer\\u2019s expectations in terms of \\u00a0 quality and satisfaction.<\\/p>\\r\\n<p align=\\"justify\\">I believe in building and sustaining \\u00a0result oriented groups of people, setting optimal performance standards for myself, individuals and teams. We believe in our people being our most important assets and at all times respect and uphold human dignity. I acknowledge that every individual has different abilities and capabilities that add to make a strong organization, for which, adequate orientation, ongoing training is provided to all \\u00a0members for enhancement of skills. One can never grow and lead if one does not know how to train.<\\/p>\\r\\n<p align=\\"justify\\">We collectively work towards better process adaptations, product improvements, aesthetic enhancement, escalating service levels and management practices while making no compromises with applicable laws, regulations and stipulations.<\\/p>\\r\\n<p align=\\"justify\\">I believe in hands on approach and encourage a keen eye for details and inculcation of in depth product knowledge to always achieve excellence, led by performance. This makes me and my entire team take pride in what we do.<\\/p>\\r\\n<p align=\\"justify\\">Striving to serve you better.<\\/p>\\r\\n<p><strong>Neeraj Jindal<\\/strong><br \\/> Managing Director<\\/p>","fulltext":"","state":1,"catid":"14","created":"2015-04-27 04:38:51","created_by":"42","created_by_alias":"","modified":"2015-06-02 07:20:56","modified_by":"42","checked_out":"42","checked_out_time":"2015-06-02 07:19:50","publish_up":"2015-04-27 04:38:51","publish_down":"0000-00-00 00:00:00","images":"{\\"image_intro\\":\\"\\",\\"float_intro\\":\\"\\",\\"image_intro_alt\\":\\"\\",\\"image_intro_caption\\":\\"\\",\\"image_fulltext\\":\\"\\",\\"float_fulltext\\":\\"\\",\\"image_fulltext_alt\\":\\"\\",\\"image_fulltext_caption\\":\\"\\"}","urls":"{\\"urla\\":false,\\"urlatext\\":\\"\\",\\"targeta\\":\\"\\",\\"urlb\\":false,\\"urlbtext\\":\\"\\",\\"targetb\\":\\"\\",\\"urlc\\":false,\\"urlctext\\":\\"\\",\\"targetc\\":\\"\\"}","attribs":"{\\"show_title\\":\\"\\",\\"link_titles\\":\\"\\",\\"show_tags\\":\\"\\",\\"show_intro\\":\\"\\",\\"info_block_position\\":\\"\\",\\"show_category\\":\\"\\",\\"link_category\\":\\"\\",\\"show_parent_category\\":\\"\\",\\"link_parent_category\\":\\"\\",\\"show_author\\":\\"\\",\\"link_author\\":\\"\\",\\"show_create_date\\":\\"\\",\\"show_modify_date\\":\\"\\",\\"show_publish_date\\":\\"\\",\\"show_item_navigation\\":\\"\\",\\"show_icons\\":\\"\\",\\"show_print_icon\\":\\"\\",\\"show_email_icon\\":\\"\\",\\"show_vote\\":\\"\\",\\"show_hits\\":\\"\\",\\"show_noauth\\":\\"\\",\\"urls_position\\":\\"\\",\\"alternative_readmore\\":\\"\\",\\"article_layout\\":\\"\\",\\"show_publishing_options\\":\\"\\",\\"show_article_options\\":\\"\\",\\"show_urls_images_backend\\":\\"\\",\\"show_urls_images_frontend\\":\\"\\"}","version":2,"ordering":"0","metakey":"","metadesc":"","access":"1","hits":"45","metadata":"{\\"robots\\":\\"\\",\\"author\\":\\"\\",\\"rights\\":\\"\\",\\"xreference\\":\\"\\"}","featured":"0","language":"*","xreference":""}', 0);
INSERT INTO `jos_ucm_history` VALUES (58, 15, 1, '', '2015-06-02 07:23:24', 42, 3074, '403e3eb5fb7871270c23f7bf067c7539b4519cfe', '{"id":15,"asset_id":"96","title":"CSR & Sustainability","alias":"csr-sustainability","introtext":"<p align=\\"justify\\"><strong>Corporate Social Responsibility and Sustainability :<\\/strong><\\/p>\\r\\n<p align=\\"justify\\"><strong>The company takes pride in its ongoing efforts towards fulfilling its responsibilities by \\u2013<\\/strong><\\/p>\\r\\n<div align=\\"justify\\">\\r\\n<ul>\\r\\n<li>Adapting processes that are least harmful to the environment<\\/li>\\r\\n<li>Minimal consumption of electricity<\\/li>\\r\\n<li>Recycling of larges masses of water without wastage. In house waste water treatment plant with zero ground disposal<\\/li>\\r\\n<li>Mass plantation of trees within its large premises and outside<\\/li>\\r\\n<li>Rain water harvesting<\\/li>\\r\\n<li>Continuous mandatory testing of raw material to ensure compliance with international chemical restrictions<\\/li>\\r\\n<\\/ul>\\r\\n<\\/div>\\r\\n<p align=\\"justify\\"><strong>Ethical Operating processes :<\\/strong><\\/p>\\r\\n<div align=\\"justify\\">\\r\\n<ul>\\r\\n<li>No Child labor employment<\\/li>\\r\\n<li>No involuntary labour<\\/li>\\r\\n<li>Stringently controlled working hours<\\/li>\\r\\n<li>Provision for safe and hygienic work environment<\\/li>\\r\\n<li>Provision for \\u00a0employment benefits<\\/li>\\r\\n<li>Extremely strict environment, human safety \\u00a0protection \\u00a0<\\/li>\\r\\n<li>Animal welfare policies<\\/li>\\r\\n<li>Promoting education, health care, eradicating hunger, poverty and malnutrition at a macro level.<\\/li>\\r\\n<\\/ul>\\r\\n<\\/div>","fulltext":"","state":1,"catid":"16","created":"2015-05-14 08:39:26","created_by":"42","created_by_alias":"","modified":"2015-06-02 07:23:24","modified_by":"42","checked_out":"42","checked_out_time":"2015-06-02 07:22:51","publish_up":"2015-05-14 08:39:26","publish_down":"0000-00-00 00:00:00","images":"{\\